import React from 'react';
import '../home.css'
const IMAGE_API = "https://image.tmdb.org/t/p/w1280";

const setVoteClass = (vote) => {
    if (vote >= 8) {
        return "green";
    }
    else if (vote >= 6) {
        return "orange";
    }
    else {
        return "red";
    }

}

const Movie = ({ title, poster_path, overview, vote_average }) => {
    return (
        <div className="card">
            <img src={
                poster_path ? (IMAGE_API + poster_path) :
                    'https://images.unsplash.com/photo-1485846234645-a62644f84728?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1040&q=80'
            } alt={title} />
            <div className="movie-info">
                <h3>
                    {title}
                </h3>
                <span className={`tag ${setVoteClass(vote_average)}`}>{vote_average}</span>                
                <span className={`tag ${setVoteClass(vote_average)}`}>like</span>                
            </div>

            <div className="movie-over">
                <h2>Overview</h2>
                <p>{overview}</p>
            </div>

        </div>
    );
};

export default Movie;
